import React, {Component} from 'react';
import AppHeader from "../AppHeader";
import SearchPanel from "../SearchPanel";
import TodoList from "../TodoList";
import './TodoApp.css';
import ItemStatusFilter from "../ItemStatusFilter";
import ItemAddForm from "../ItemAddForm";

class TodoListApp extends Component
{
    maxId = 100;
    createNewItem = (label) => {
        return {
            label: label,
            important: false,
            isDone: false,
            id: ++this.maxId
        };
    };
    state = {
        todoData: [
            this.createNewItem('Drink coffee'),
            this.createNewItem('Work hard')
        ]
    };

    deleteItem = (id) => {
        this.setState(({todoData}) => {
            const idx = todoData.findIndex((el) => el.id === id);
            let newTodoData = [...todoData].filter((element, index) => index !== idx);
            return {
              todoData: newTodoData
            };
        });
    };

    addItem = (text) => {
        const newItem = this.createNewItem(text);

        this.setState(({todoData}) => {
            let newTodoData = [...todoData, newItem];
            return {
                todoData: newTodoData
            };
        });
    };

    toggleDone = (id) => {
        this.setState(({todoData}) => {
            const idx = todoData.findIndex((el) => el.id === id);
            let oldTodoDataItem = todoData[idx];
            let newTodoDataItem = {...oldTodoDataItem,isDone: !oldTodoDataItem.isDone};
            let newTodoData = [
                ...todoData.slice(0,idx),
                newTodoDataItem,
                ...todoData.slice(idx + 1),
            ];
            return {
                todoData: newTodoData
            };
        });
    };

    toggleImportant = (id) => {
        this.setState(({todoData}) => {
            const idx = todoData.findIndex((el) => el.id === id);
            let oldTodoDataItem = todoData[idx];
            let newTodoDataItem = {...oldTodoDataItem,important: !oldTodoDataItem.important};
            let newTodoData = [
                ...todoData.slice(0,idx),
                newTodoDataItem,
                ...todoData.slice(idx + 1),
            ];
            return {
                todoData: newTodoData
            };
        });
    };

    render() {
        const doneCount = this.state.todoData.filter((el)=>el.isDone).length;
        return (
            <div className="todo-app">
                <AppHeader toDo={1} done={doneCount}/>
                <div className="top-panel d-flex">
                    <SearchPanel/>
                    <ItemStatusFilter filter="all"/>
                </div>
                <TodoList
                    todos={this.state.todoData}
                    onDeleted={this.deleteItem}
                    onToggleDone={this.toggleDone}
                    onToggleImportant={this.toggleImportant}
                />
                <ItemAddForm onItemAdded = {this.addItem}/>
            </div>);
    };
}

export default TodoListApp;
