import React, {Component} from "react";
import './TodoListItem.css';

export default class TodoListItem extends Component {
    state = {
        done: false,
        important: false
    };

    onLabelClick = () => {
        this.setState((state) => {
            return {
                done: !state.done
            };
        });
    };

    taskImportantToggle = () => {
        this.setState((state)=>{
            return {
                important: !state.important
            };
        });
    };

    render() {
        const {label} = this.props;
        const {done: isDone, important = false} = this.state;
        const labelClasses = "todo-list-item-label " + (important ? "todo-list-item-text-important" : "");
        const labelClassNames = "todo-list-item" + (isDone ? " done" : "");
        return (
            <span className={labelClassNames}>
              <span
                  className={labelClasses}
                  onClick={this.onLabelClick}
              >{label}</span>

              <button type="button"
                      className="btn btn-outline-success btn-sm float-right"
                      onClick={this.taskImportantToggle}
              >
                <i className="fa fa-exclamation"></i>
              </button>

              <button type="button"
                      className="btn btn-outline-danger btn-sm float-right">
                <i className="fa fa-trash-o"></i>
              </button>
        </span>);
    };
}
